import React, { useEffect, useState } from "react";
import "./styles/UserList.scss"
import images from '../images/Images'
import crown from '../images/crown.png'

const UserList = ({ socket }: any) => {
	const [userList, setUserList] = useState<{id: string, icon:string, status:string}[]>([{id:'jinkim', icon:'gamer_boy', status:'owner'}, {id:'2222', icon:'woman_icon', status:'administrator'}, {id:'3333', icon:'man_icon', status:'none'}]);

	useEffect(() => {
		socket.on("UserList", (newUser:{id: string, icon:string, status:string}[]) => {
			setUserList(newUser);
		});

		return () => {
			socket.disconnect();
		};
	}, [socket]);

	return (
		<div className="userList-container">
			{userList?.map(user=>
			<div>
			<button className="userList-btn">
				<span><img src={images(user.icon)} width="30" height="30" className="user-icon"/></span>
				{user.status !== 'none' ?
					<img src={crown} width="20" height="20" className={user.status === 'owner' ? 'owner' : 'administrator'}/>
					: null
				}
				<span className='user-id'>{user.id}</span>
			</button>
			</div>
			)}
		</div>

	);
  };
  export default UserList;
