import React, { useEffect, useState } from "react";
import "./styles/UserList.scss"
import images from '../images/Images'

const UserList = ({ socket }: any) => {
	const [userList, setUserList] = useState<{id: string, icon:string, status:string}[]>([{id:'jinkim', icon:'gamer_boy', status:'owner'}, {id:'2222', icon:'woman_icon', status:'administrator'}, {id:'3333', icon:'man_icon', status:'none'}]);

	useEffect(() => {
		socket.on("UserList", (newUser:{id: string, icon:string, status:string}[]) => {
			setUserList(newUser);
		});

		return () => {
			socket.disconnect();
		};
	}, [socket]);

	return (
		<div>
			{userList?.map(user=>
			<div>
			<button className="userList-btn">
				<span>
					<img src={images(user.icon)}  width="20" height="20" className="icon"/>
				</span>
				<span>{user.id}</span>
				<span className="userInfo">
					<div className="id">{user.id}</div>
				</span>
			</button>
			</div>
			)}
		</div>

	);
  };
  export default UserList;
