import React, { useCallback, useEffect, useState } from "react";
import "./styles/UserList.scss"
import images from '../images/Images'
import crown from '../images/crown.png'
import { string } from "yargs";

const UserList = ({ socket }: any) => {
	const [userList, setUserList] = useState<{id: string, icon:string, status:string}[]>([{id:'2222', icon:'woman_icon', status:'administrator'}, {id:'3333', icon:'man_icon', status:'none'}, {id:'jinkim', icon:'gamer_boy', status:'owner'}]);

	useEffect(() => {
		socket.on("UserList", (newUser:{id: string, icon:string, status:string}[]) => {
			setUserList(newUser);
		});

		return () => {
			socket.disconnect();
		};
	}, [socket]);

	function userSorting()
	{
		const temp = userList;
		setUserList([]);

		const owner = temp.find((ele) => {
			if (ele.status === 'owner')
				return true;
		})
		setUserList((userList) => [...userList, {id:owner.id, icon:owner.icon, status:owner.status} as never]);
	}

	return (
		<div className="userList-container">
			{userList?.map(user=>
			<div>
			<button className="userList-btn">
				<span className="iconBox">
					<span><img src={images(user.icon)} width="30" height="30" className="icon"/></span>
					<span>
					{user.status !== 'none' ?
						<img src={crown} width="20" height="20" className={user.status === 'owner' ? 'crown owner' : 'crown administrator'}/>
						: null
					}
					</span>
				</span>
				<span className='id'>{user.id}</span>
			</button>
			</div>
			)}
		</div>

	);
  };
  export default UserList;
